import EventEmitter from "eventemitter3";

class Client {
  _subscriptions: {
    [x: string]: any;
  };

  _emitter: EventEmitter<string | symbol, any>;

  constructor() {
    this._subscriptions = {};
    this._emitter = new EventEmitter();
  }

  subscribe(topic = "/") {
    if (this._subscriptions[topic]) {
      console.log("a");
      return this._subscriptions[topic];
    }
    console.log("b");
    this._subscriptions[topic] = new BroadcastChannel(topic);
    this._subscriptions[topic].onmessage = this._handleOnMessage.bind(this);
  }

  _handleOnMessage(args: MessageEvent) {
    const { data } = args;
    console.log("_handleOnMessage", data);
    const { eventName, payload } = data;
    this._emitter.emit(eventName, payload);
  }

  on(eventName: string, listener: (...args: any[]) => void) {
    this._emitter.on(eventName, listener);
  }

  once(eventName: string, listener: (...args: any[]) => void) {
    this._emitter.once(eventName, listener);
  }

  off(eventName: string, listener: (...args: any[]) => void) {
    this._emitter.off(eventName, listener);
  }

  isSubscrubed(topic = undefined) {
    if (topic) {
      return Object.keys(this._subscriptions[topic!]);
    } else {
      return Object.keys(this._subscriptions);
    }
  }

  send(topic = "/", payload: any) {
    console.log("send");
    new BroadcastChannel(topic).postMessage(payload);
  }

  publish(topic = "/", payload: any) {
    this.send(topic, payload);
  }

  disconnect(topic = undefined) {
    if (topic) {
      this._subscriptions[topic!].close();
    } else {
      for (let o in this._subscriptions) {
        o.close();
      }
    }
  }
}

const cb = new Client();
export default cb;
